INSERT INTO `wp_posts` VALUES("33", "1", "2019-07-24 15:18:19", "2019-07-24 13:18:19", "<!-- wp:paragraph -->\n<p>gleich den süßen Frühlingsmorgen, die ich mit ganzem Herzen genieße. Ich bin allein und freue mich meines Lebens in dieser Gegend, die für solche Seelen geschaffen ist wie die meine. Ich bin so glücklich, mein Bester, so ganz in dem Gefühle von ruhigem Dasein versunken, daß meine Kunst darunter leidet. Ich könnte jetzt nicht zeichnen, nicht einen Strich, und bin nie ein größerer Maler gewesen als in diesen Augenblicken.&nbsp;</p>\n<!-- /wp:paragraph -->", "Eine wunderbare Heiterkeit hat meine ganze Seele eingenommen,", "", "publish", "open", "open", "", "eine-wunderbare-heiterkeit-hat-meine-ganze-seele-eingenommen", "", "", "2019-07-24 15:18:19", "2019-07-24 13:18:19", "", "0", "http://localhost:8888/?p=33", "3", "post", "", "0");
